import React, { useState, useEffect } from 'react'
import { StyleSheet, View, Text, Image, FlatList, Button, Platform } from 'react-native'

import firebase from 'firebase'
require('firebase/firestore')
import { connect } from 'react-redux'
import * as ImagePicker from 'expo-image-picker';


function Profile(props) {
    const [userPosts, setUserPosts] = useState([]);
    const [user, setUser] = useState(null);
    const [following, setFollowing] = useState(false);
    const [follower, setFollower] = useState(0);
    const [asfollower, setasFollower] = useState(0);
    const [postSize, setPostSize] = useState(0);
    const [hasGalleryPermission, setHasGalleryPermission] = useState(null);
    const [permissionImage, setPermissionImage] = useState(null);
    const [image, setImage] = useState(null)

    useEffect(() => {
        const { currentUser, posts } = props;

        if (props.route.params.uid === firebase.auth().currentUser.uid) {
            setUser(currentUser)
            setUserPosts(posts)
            firebase.firestore().collection("following").doc(firebase.auth().currentUser.uid).collection("userFollowing").get()
            .then(snapshot => {
                setFollower(snapshot.size);
            })
            firebase.firestore().collection("follower").doc(props.route.params.uid).collection("asFollower").get()
            .then(snapshot => {
                setasFollower(snapshot.size);
            })
            posts ? setPostSize(posts.length) : null
        }
        else {
            firebase.firestore()
                .collection("users")
                .doc(props.route.params.uid)
                .get()
                .then((snapshot) => {
                    if (snapshot.exists) {
                        setUser(snapshot.data());
                    }
                    else {
                        console.log('does not exist')
                    }
                })
            firebase.firestore()
                .collection("posts")
                .doc(props.route.params.uid)
                .collection("userPosts")
                .orderBy("creation", "asc")
                .get()
                .then((snapshot) => {
                    let posts = snapshot.docs.map(doc => {
                        const data = doc.data();
                        const id = doc.id;
                        return { id, ...data }
                    })
                    setUserPosts(posts)
                    posts ? setPostSize(posts.length) : null 
                })

                firebase.firestore().collection("following").doc(props.route.params.uid).collection("userFollowing").get()
                .then(snapshot => {
                    setFollower(snapshot.size);
                })
                firebase.firestore().collection("follower").doc(props.route.params.uid).collection("asFollower").get()
                .then(snapshot => {
                    setasFollower(snapshot.size);
                })
        }

        if (props.following.indexOf(props.route.params.uid) > -1) {
            setFollowing(true);
        } else {
            setFollowing(false);
        }

        (async () => {

            const galleryStatus = await ImagePicker.requestCameraRollPermissionsAsync();
            setHasGalleryPermission(galleryStatus.status === 'granted');

        })();

        if(Platform.OS != 'web') {
            if (hasGalleryPermission === null || hasGalleryPermission === false) {
                setPermissionImage(true)
            }
        }

    }, [props.route.params.uid, props.following])

    const onFollow = () => {
        firebase.firestore()
            .collection("following")
            .doc(firebase.auth().currentUser.uid)
            .collection("userFollowing")
            .doc(props.route.params.uid)
            .set({})

        firebase.firestore()
            .collection("follower")
            .doc(props.route.params.uid)
            .collection("asFollower")
            .doc(firebase.auth().currentUser.uid)
            .set({})
    }
    const onUnfollow = () => {
        firebase.firestore()
            .collection("following")
            .doc(firebase.auth().currentUser.uid)
            .collection("userFollowing")
            .doc(props.route.params.uid)
            .delete()

        firebase.firestore()
            .collection("follower")
            .doc(props.route.params.uid)
            .collection("asFollower")
            .doc(firebase.auth().currentUser.uid)
            .delete()
    }

    const onLogout = () => {
        firebase.auth().signOut();
    }

    const trash = (downloadURL) => {
        firebase.firestore()
            .collection('posts')
            .doc(firebase.auth().currentUser.uid)
            .collection('userPosts')
            .doc(downloadURL)
            .delete()
    }

    const ChangeProfile = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            allowsEditing: true,
            aspect: [1, 1],
            quality: 1,
        });
        console.log(result);

        if (!result.cancelled) {
            setImage(result.uri);
            const uri = image;
            const childPath = `post/${firebase.auth().currentUser.uid}/${Math.random().toString(36)}`;
            console.log(childPath)

            const response = await fetch(uri);
            const blob = await response.blob();

            const task = firebase
                .storage()
                .ref()
                .child(childPath)
                .put(blob);

            const taskProgress = snapshot => {
                console.log(`transferred: ${snapshot.bytesTransferred}`)
            }

            const taskCompleted = () => {
                task.snapshot.ref.getDownloadURL().then((snapshot) => {
                    savePostData(snapshot);
                    console.log(snapshot)
                })
            }

            const taskError = snapshot => {
                console.log(snapshot)
            }

            task.on("state_changed", taskProgress, taskError, taskCompleted);
        }
    }

    if (user === null) {
        return <View />
    }

    return (
        <View style={styles.container}>
            <View style={styles.containerInfo}>
                {console.log(props.route.params.image)}
                {user.picture ? (
                    <Image
                        source={{uri: user.picture}}
                        style={styles.picture}
                    />
                    ) : <Image 
                            source={require('../../assets/favicon.png')}
                            style={styles.picture}
                        />}
                <Text>{user.name}</Text>
                <Text>{user.email}</Text>

                {props.route.params.uid !== firebase.auth().currentUser.uid ? (
                    <View>
                        {following ? (
                            <Button
                                title="Following"
                                onPress={() => onUnfollow()}
                            />
                        ) :
                            (
                                <Button
                                    title="Follow"
                                    onPress={() => onFollow()}
                                />
                            )}
                    </View>
                ) :
                    <Button
                        title="Logout"
                        onPress={() => onLogout()}
                    />}
                <Text>Following : {follower}</Text>
                <Text>Follower : {asfollower}</Text>
                <Text>Posts: {postSize}</Text>

                {permissionImage ? null : (
                    <Button
                        title='Change profile picture'
                        onPress={() => ChangeProfile()}
                    />
                )}
            </View>

            <View style={styles.containerGallery}>
                <FlatList
                    numColumns={3}
                    horizontal={false}
                    data={userPosts}
                    renderItem={({ item }) => (
                        <View
                            style={styles.containerImage}>

                            <Image
                                style={styles.image}
                                source={{ uri: item.downloadURL }}
                            />

                            <Image 
                                source={{ uri: './assets/croix.png'}}
                                style={{flex: 1, width: 64, height: 64, margin: 2, alignItems: 'center'}}
                                onPress={() => trash(item.downloadURL)}
                            />
                        </View>

                    )}

                />
            </View>
        </View>

    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    containerInfo: {
        margin: 20
    },
    containerGallery: {
        flex: 1
    },
    containerImage: {
        flex: 1 / 3

    },
    image: {
        flex: 1,
        aspectRatio: 1 / 1
    },
    picture: {
        flex: 1,
        width: 48,
        height: 48,
        borderRadius: 50
    }
})
const mapStateToProps = (store) => ({
    currentUser: store.userState.currentUser,
    posts: store.userState.posts,
    following: store.userState.following
})
export default connect(mapStateToProps, null)(Profile);
