import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Image, Button, FlatList, StyleSheet } from 'react-native';

import firebase from 'firebase';
require('firebase/firestore')

import { connect } from 'react-redux';
import { bindActionCreators } from 'redux'
import { fetchUsersData } from '../../redux/actions/index'

function Setting(props) {

    const [user, SetUser] = useState(null);

    useEffect(() => {
        firebase.firestore()
        .collection("users")
        .doc(firebase.auth().currentUser.uid)
        .get()
        .then((snapshot) => {
            if (snapshot.exists) {
                SetUser(snapshot.data())
            } else {
                console.log("Setting user does not exist")
            }
        })
    }, [props])

    if (user === null) {
        return <View />
    }

    return (
        <View style={styles.container}>
             {user.picture ? (
                    <Image
                        source={{uri: user.picture}}
                        style={{width: 48, height: 48, borderRadius: 50}}
                    />) : (
                    <Image
                        source={require('../../assets/friend.png')}
                        style={{width: 48, height: 48, borderRadius: 50}}
                    />
                )}  
            <Text>{user.name}</Text>
            <Text>{user.email}</Text>
            <Text>{props.users.name}</Text>
            <FlatList
                  numColumns={3}
                  horizontal={false}
                  data={props.users}
                  renderItem={({ item }) => {
                    <View>
                        <Text>Follower : </Text>
                        <Image
                            source={require('../../assets/croix.png')}
                            style={{flex: 1, width: 64, height: 64}}
                        />
                        <Text>{item}</Text>

                    </View>
                    {console.log(item)}
                  }}
            />
        </View>
    )

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        textAlign: 'center',
        padding: 25
    }
})

const mapStateToProps = (store) => ({
    users: store.usersState.users
})
const mapDispatchProps = (dispatch) => bindActionCreators({ fetchUsersData }, dispatch);

export default connect(mapStateToProps, mapDispatchProps)(Setting);
